import "./bookticket.css";
import React, { useEffect } from "react";
import { useState, useRef } from "react";
import { Movies } from "./Moviedata";
import Image1 from "../images/movies_screen/screen.png";
import { useParams } from "react-router-dom";
import { useNavigate } from "react-router-dom";

import "bootstrap/dist/css/bootstrap.min.css";

import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";

export default function Bookticket(props) {
  const [datm, setdatm] = useState(["", "", "", "", "", "", "", ""]);
  const [book, setBook] = useState([]);

  const user = JSON.parse(localStorage.getItem("user"));

  // console.log("usergetValueeeeee====>", user);

  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const [mov, setmov] = useState([]);

  const { movieid } = useParams();
  // console.log("movieeid===>", movieid);

  useEffect(() => {
    const filter = Movies.filter((data) => {
      return data.id == movieid;
    });

    setmov(filter[0]);
  }, []);

  // console.log("selectedMoviee====>", mov);

  const bookseat = (event, index) => {
    console.log("eeee===>", event, index);
    if (datm[index] == "") {
      datm[index] = "1";
      event.target.style.background = "yellow";
    }
    let j = [];
    for (var i = 0; i < 8; i++) {
      if (datm[i] == "1") {
        j = j + i;
      }
    }

    setBook(j);
  };

  console.log(book);

  const ref = useRef(null);
  console.log(ref);
  const [count, setCount] = useState(0);
  const [Price, setPrice] = useState(0);
  function changecolor(e) {
    if (e.target.className !== "row") {
      if (e.target.style.background !== "green") {
        // e.target.style.background = "green";
        setCount(count + 1);
        setPrice(Price + 150);
      } else {
        e.target.style.background = " #01163E";
        setCount(count - 1);
        setPrice(Price - 150);
      }
    }
  }
  const navigate = useNavigate();

  return (
    <body1>
      <div className="container1">
        <div className="cards1">
          <div className="card1">
            <img className="img" src={mov?.posterUrl} />
          </div>
        </div>

        <div className="row-container">
          <ul className="showcase">
            <li>
              <div className="seat selected"></div>
              <small>Selected</small>
            </li>
            <li>
              <div className="seat occupied"></div>
              <small>Occupied</small>
            </li>
          </ul>
          <div className="movie-screen">
            <img src={Image1} alt="screen" />
          </div>
          <div onClick={changecolor} className="row">
            <div
              className="seat"
              value="a1"
              onClick={(e) => {
                bookseat(e, 0);
              }}
            ></div>
            <div
              className="seat"
              value="a1"
              onClick={(e) => {
                bookseat(e, 1);
              }}
            ></div>
            <div
              className="seat"
              value="a1"
              onClick={(e) => {
                bookseat(e, 2);
              }}
            ></div>
            <div
              className="seat"
              value="a1"
              onClick={(e) => {
                bookseat(e, 3);
              }}
            ></div>
            <div
              className="seat"
              value="a1"
              onClick={(e) => {
                bookseat(e, 4);
              }}
            ></div>
            <div
              className="seat"
              value="a1"
              onClick={(e) => {
                bookseat(e, 5);
              }}
            ></div>
            <div
              className="seat"
              value="a1"
              onClick={(e) => {
                bookseat(e, 6);
              }}
            ></div>
            <div
              className="seat"
              value="a1"
              onClick={(e) => {
                bookseat(e, 7);
              }}
            ></div>
          </div>
          <div onClick={changecolor} className="row">
            <div className="seat" value="a1"></div>
            <div className="seat" value="a1"></div>
            <div className="seat" value="a1"></div>
            <div className="seat" value="a1"></div>
