import React, { useEffect } from 'react'
import FormInput from "./FormInput";
import { useState } from "react";
import "./signup.css";
import { useParams } from "react-router-dom";

import { Movies } from "./Moviedata";

function Bookedmenu() {

const {price}= useParams();

  const {count}= useParams();


  console.log("priceeeee===>",price);
  console.log("counteeeee===>",count);
 
  const booking = JSON.parse(localStorage.getItem("booking"));

console.log("booking==>", booking);

const [mov, setmov] = useState([]);

const { movieid } = useParams();

useEffect(() => {
  const filter = Movies.filter((data) => {
    return data.id == movieid;
  });

  setmov(filter[0]);
for(var i=0;i<booking.length;i++){

}






}, []);

console.log("selectedMoviee====>", mov);




    const [values, setValues] = useState({
        username: "",
        email: "",
      
        phonn: "",
        confirmPassword: "",
      });
    
      const inputs = [
        {
          id: 1,
          name: "username",
          type: "text",
          placeholder: "Username",
          errorMessage:
            "Username should be 3-16 characters and shouldn't include any special character!",
          label: "Username",
          pattern: "^[A-Za-z0-9]{3,16}$",
          required: true,
        },
        {
          id: 2,
          name: "email",
          type: "email",
          placeholder: "Email",
          errorMessage: "It should be a valid email address!",
          label: "Email",
          required: true,
        },
    
        {
          id: 4,
          name: "phonenumber",
          type: "text",
          placeholder: "Phone Number",
          errorMessage:
            "Phone Number should Have 10 Number",
          label: "phone",
          pattern: `/^\(?(\d{3})\)?[- ]?(\d{3})[- ]?(\d{4})$/`,
          required: true,
        },
       
        {
            id: 4,
            name: "cardnumber",
            type: "text",
            placeholder: "Credit/Debit  Card No",
            errorMessage:
              "Starting with 34 or 37, length 15 digits",
            label: "",
            pattern: ` /^(?:3[47][0-9]{13})$/`,
            required: true,
          },
      
      ];
    
      const handleSubmit = (e) => {
        e.preventDefault();
      };
    
      const onChange = (e) => {
        setValues({ ...values, [e.target.name]: e.target.value });
    
      };
      // console.log("login===>",values); 
      // localStorage.setItem("user", values);
      localStorage.setItem("user", JSON.stringify(values));
    
    
    
  return (
    <div className="formapp2">

<div className="disply">
    <div className="image">
        <img src={mov?.posterUrl} />
        <h2>{mov?.title}</h2>


<h3> Seats Numbers : {booking.slice(1)} </h3>
<h3>Tickets : {count.slice(1)}</h3>
<h2>Amount : {price.slice(1)}</h2>

    </div>



</div>



    <form className='form1' onSubmit={handleSubmit}>
           <h1>Book Tickets</h1>
           {inputs.map((input) => (
             <FormInput
               key={input.id}
               {...input}
               value={values[input.name]}
               onChange={onChange}
             />
           ))}
           <button>Confirm</button>
         </form>
         </div>
  )
}

export default Bookedmenu
